#include <iostream>
#include <string>
#include <cstdlib>

int checkDigits(std::string s, int offset1, int offset2) {
    int digit1 = s[offset1] - '0';
    int digit2 = s[offset2] - '0';

    return digit1 == digit2 ? digit1 : 0;
}

int part1(std::string s) {

    int sum = 0, digit1, digit2;
    
    for (int i = 1; i < s.length(); ++i) {
        sum += checkDigits(s, i, i-1);
    }
    sum += checkDigits(s, 0, s.length()-1);

    return sum;
}

int part2(std::string s) {

    int sum = 0, digit1, digit2;
    int length = s.length();

    for (int i = 0; i < length; ++i) {
        int offset1 = i;
        int offset2 = (i + (length/2)) % length;
        sum += checkDigits(s, offset1, offset2);
    }

    return sum;
}

int main() {

    std::string input = "";

    std::cout << "Part 1: " << part1(input) << std::endl;
    std::cout << "Part 2: " << part2(input) << std::endl;



    //doPart2();

    return 0;
} 
